import React, { useState, useEffect } from 'react';
import MovieSetup from "./MovieSetup";

function List(props) {
  const [addingMovie, setAddingMovie] = useState(false);
  const [click, setClick] = useState([]);
  
  useEffect((movie) => {
    console.log(movie)
    if (movie == undefined) {
      return
    }

    setAddingMovie(true);
  }, [click]);

    return (
      <div className="item-container">
        {props.items.map((movie, index) => 
        (
          <div key={index} className="items">
            <img onClick={() => setClick(movie)} src={movie.Poster} alt='movie' className='movie-image'></img>
            <div className="overlay"></div>
            <MovieSetup trigger={addingMovie} setTrigger={setAddingMovie} addMovie={props.addMovie}  setAddMovie={props.setAddMovie} RnR={props.RnR} setRnR={props.setRnR} movie={movie}/>
          </div>
        ))}
      </div>
    )
}

export default List;
