import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";

const server_url = 'http://localhost:11037';

function SignUp() {
  const [newUsername, setUsername] = useState('');
  const [newPassword, setPassword] = useState('');
  const [submit, setSubmit] = useState(false);
  const [error, setError] = useState('');

  const navigate = useNavigate();
  
  useEffect(() => {
    const createUser = async () => {
        try {
            const options = {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({"username": newUsername,
                                      "password": newPassword
                                    })
            }
            console.log(newUsername);
            if (newUsername === '') {
                const response = await fetch(server_url + '/users', options);
                const data = await response.json();
                console.log(data);
                navigate("/login");
                setUsername('');
                setPassword('');
            }
        } catch (error) {
            console.error(error.message);
        }
    };

    // Call the createList function to fetch the lists
    createUser();
    // eslint-disable-next-line
}, [submit]);

  return (
    <div className="CreateList">
      <h1 className="Large">Welcome to Rankd</h1>
      <h2 >Sign Up</h2>
      <h2>Title</h2>
      <input className="title" value={newUsername || ''} onChange={(event) => setUsername(event.target.value)}></input>
      <h2>Description</h2>
      <textarea className="title" value={newPassword || ''} onChange={(event) => setPassword(event.target.value)}></textarea>
      <button onClick={setSubmit}>Create List</button>
    </div>
  )
}

export default SignUp;
