import React, { useState, useEffect } from 'react';
import MovieSetup from "./MovieSetup";

function List(props) {
  const [addingMovie, setAddingMovie] = useState(false);
  const [click, setClick] = useState(false);
  let currentMovie;
  
  useEffect((movie) => {
    console.log(movie)
    if (movie == false) {
      setAddingMovie(true);
      currentMovie = movie
    }
}, [click]);

    return (
      <div className="item-container">
        {props.items.map((movie, index)=> (
          <div key={index} className="items">
            <img onClick={() => setClick(movie)} src={movie.Poster} alt='movie' className='movie-image'></img>
            <div className="overlay"></div>
          </div>
        ))}
        <MovieSetup trigger={addingMovie} setTrigger={setAddingMovie} addMovie={props.addMovie}  setAddMovie={props.setAddMovie} RnR={props.RnR} setRnR={props.setRnR} movie={currentMovie}/>
      </div>
    )
}

export default List;
