import React, { useState, useEffect } from 'react';
import './App.css';
import PageH from "./Components/PageHeader";
import List from "./Components/List";

function App() {
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  const getMovieRequest = async (searchValue) => {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: searchValue,
      credentials: 'include'
    }

    try {
      const res = await fetch('http://localhost:11037/query/movies', options);

      if (!res.ok) {
        throw new Error(`HTTP error! Status: ${res.status}`);
      }

      const data = await res.json();

      if (data.Search) {
        setMovies(data.Search);
      } else {
        console.error('No "Search" property found in the response:', data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  }

  useEffect(() => {
    getMovieRequest(searchValue);
  }, [searchValue]);

  return (
    <div className="App">
      <PageH heading="Rankd" searchValue={searchValue} setSearchValue={setSearchValue}/>
      <div className="item-container">
        <List items={movies}/>
      </div>
    </div>
  );
}

export default App;
