import React, { useState, useEffect } from 'react';
import MovieSetup from "./MovieSetup";

function List(props) {
  const [addingMovie, setAddingMovie] = useState(false);
  let currentMovie;
  
  const onClickEvent = (movie) => {
    setAddingMovie(true);
    currentMovie = movie
  }

    return (
      <div className="item-container">
        {props.items.map((movie, index)=> (
          <div key={index} className="items">
            <img onClick={onClickEvent(movie)} src={movie.Poster} alt='movie' className='movie-image'></img>
            <div className="overlay"></div>
          </div>
        ))}
        <MovieSetup trigger={addingMovie} setTrigger={setAddingMovie} addMovie={props.addMovie}  setAddMovie={props.setAddMovie} RnR={props.RnR} setRnR={props.setRnR}/>
      </div>
    )
}

export default List;
