import React, { useState, useEffect } from 'react';
import MovieSetup from "./MovieSetup";

function List(props) {
  const [addingMovie, setAddingMovie] = useState(false);
  const [clickedMovie, setClickedMovie] = useState(null);

  const openPopup = (movie) => {
    setClickedMovie(movie);
    console.log(clickedMovie);
  };

  const closePopup = () => {
    setClickedMovie(null);
  };

    return (
      <div className="item-container">
        {props.items.map((movie, index) => 
        (
          <div key={index} className="items">
            <img onClick={() => openPopup(movie)} src={movie.Poster} alt='movie' className='movie-image'></img>
            <div className="overlay"></div>
            {clickedMovie === movie && (<MovieSetup trigger={true} setTrigger={closePopup} addMovie={props.addMovie}  setAddMovie={props.setAddMovie} RnR={props.RnR} setRnR={props.setRnR} movie={movie}/>)}
          </div>
        ))}
      </div>
    )
}

export default List;
