import React, { useState, useEffect } from 'react';
import './App.css';
import PageH from "./Components/PageHeader";
import Home from "./Components/Home";
import SignIn from "./Components/SignIn";
import SignUp from "./Components/SignUp";
import Lists from "./Components/Lists";

const server_url = 'http://localhost:11037';

function App() {
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  const getMovieRequest = async (searchValue) => {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'text/plain',
      },
      body: searchValue
    }

    const response = await fetch(server_url + '/query/movies', options);

    const res = await response.json();

    console.log(res);

    if (res.Search) {
      setMovies(res.Search);
    }
  }

  useEffect(() => {
    getMovieRequest(searchValue);
  }, [searchValue]);

  //
  // Different Windows
  //

  const renderComponent = () => {
    try {
      if (window.location.pathname === '/signin') {
        return <SignIn />;
      } else if (window.location.pathname === '/signup') {
        return <SignUp />;
      } else if (window.location.pathname === '/lists') {
        return <Lists movies={movies} />;
      } else {
        return <Home />;
      }
    } catch (error) {
      console.error("Error in renderComponent:", error);
      // Handle the error gracefully
    }

  }

  return (
    <div className="App">
      <PageH heading="Rankd" searchValue={searchValue} setSearchValue={setSearchValue}/>

      {renderComponent()}
    </div>
  );
}

export default App;
