import React, { useEffect } from 'react';
import List from "./List";
import { Route, Routes, Link, useParams} from 'react-router-dom';

const server_url = 'http://localhost:11037';

function ListDetails(props) {
    const { title } = useParams(); // Get the list title from the URL params
    const list = props.lists.find(list => list.title === title); // Find the corresponding list based on the title

    if (!list) {
        return <div>List not found</div>;
    }

    return (
        <div>
            <h1>{list.title}</h1>
            <h3>{list.text}</h3>
            <p>Created By: {list.username}</p>
        </div>
    );
}

function Lists(props) {

    useEffect(() => {
        // Define an async function to fetch the lists
        const fetchLists = async () => {
            try {
                const options = {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    }
                }
                const response = await fetch(server_url + '/lists', options);
                const data = await response.json();
                props.setLists(data); // Update the state with the fetched lists
            } catch (error) {
                console.error('Error fetching lists:', error);
            }
        };

        // Call the fetchLists function to fetch the lists
        fetchLists();
    }, []);

    return (
        <div className="homepage">
            <h1 className="">Live User Lists</h1>
            
            <Routes>
                <Route path={'/lists/:title'} element={<ListDetails lists={props.lists} />}/>
            </Routes>
            

            <div className="lists-container">
                <table>
                    <tr>
                        <th>List</th>
                        <th>Description</th>
                        <th>Created By</th>
                    </tr>
                    {props.lists.map((list, index)=> (
                        <tr key={index}>
                            <td><Link to={"/lists/" + list.title} className="black-text"><h1>{list.title}</h1></Link></td>
                            <td><h3>{list.text}</h3></td>
                            <td><p>{list.username}</p></td>
                        </tr>
                    ))}
                </table>
            </div>

            <List items={props.movies} />
        </div>
    )
}

export default Lists;
