import React, { useState, useEffect } from 'react';

function MovieSetup(props) {
    const [newRank, setRank] = useState(0);
    const [newReview, setReview] = useState('');

    const submitMovie = () => {
        if (newRank) {
            console.log(newRank);
            props.setRnR({"rank": newRank, "review": newReview});
            props.setTrigger(false);
        } else {
            // Enter Valid Rank
        }
    }
    
    return (props.trigger) ? (
      <div className="pop">
        <div className="pop-in">
            <div className="setRank">
                <h1>Set Rank</h1>
                <input type="number" value={newRank} onChange={(event) => setRank(event.target.value)}></input>
            </div>

            <div className="setReview">
                <h1>Optional: Create Review</h1>
                <input value={newReview} onChange={(event) => setReview(event.target.value)}></input>
            </div>

            <button onClick={submitMovie()}>Add Movie</button>
        </div>
      </div>
    ) : ''
}

export default MovieSetup;
