import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";

function SignUp() {
  const [newUsername, setUsername] = useState();
  const [newPassword, setPassword] = useState();
  const [submit, setSubmit] = useState(false);
  
  useEffect(() => {
    const createList = async () => {
        try {
            const options = {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({"user": "651ce4f5524d7a8b6ca23ac9",
                                      "title": newTitle,
                                      "text": newDesc
                                    })
            }
            console.log(newTitle);
            if (newTitle.localeCompare('')) {
                const response = await fetch(server_url + '/lists', options);
                const data = await response.json();
                console.log(data);
                navigate("/"+newTitle);
                setTitle('');
                setDesc('');
            }
        } catch (error) {
            console.error('Error creatin list:', error);
        }
    };

    // Call the createList function to fetch the lists
    createList();
    // eslint-disable-next-line
}, [submit]);

  return (
    <div className="CreateList">
      <h1 className="Large">Welcome to Rankd</h1>
      <h2 >Sign Up</h2>
      <h2>Title</h2>
      <input className="title" value={newUsername || ''} onChange={(event) => setUsername(event.target.value)}></input>
      <h2>Description</h2>
      <textarea className="title" value={newPassword || ''} onChange={(event) => setPassword(event.target.value)}></textarea>
      <button onClick={setSubmit}>Create List</button>
    </div>
  )
}

export default SignUp;
