import React, { useState, useEffect } from 'react';
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import './App.css';
import PageH from "./Components/PageHeader";
import Home from "./Components/Home";
import SignIn from "./Components/SignIn";
import SignUp from "./Components/SignUp";
import Lists from "./Components/Lists";

const server_url = 'http://localhost:11037';

function App() {
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  const getMovieRequest = async (searchValue) => {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'text/plain',
      },
      body: searchValue
    }

    const response = await fetch(server_url + '/query/movies', options);

    const res = await response.json();

    console.log(res);

    if (res.Search) {
      setMovies(res.Search);
    }
  }

  useEffect(() => {
    getMovieRequest(searchValue);
  }, [searchValue]);

  return (
    <div className="App">
      <PageH heading="Rankd" searchValue={searchValue} setSearchValue={setSearchValue}/>

      <Routes>
        <Route path="/" exact>
          <Home />
        </Route>

        <Route path="/signin" >
          <SignIn />
        </Route>

        <Route path="/signup" >
          <SignUp />
        </Route>

        <Route path="/lists" >
          <Lists movies={movies}/>
        </Route>
      </Routes>
    </div>
  );
}

export default App;
