import React, { useState, useEffect } from 'react';
import Search from "./SearchBar";
import List from "./List";
import DisplayMovie from "./DisplayMovie";

const server_url = 'http://localhost:11037';

function Rank(props) {
  const [addMovie, setAddMovie] = useState([]);
  const [RankandReview, setRnR] = useState({"rank": 0, "review": ''});

  const getUserByID = async (user) => {
    const options = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({"user": user})
    }

    const response = await fetch(server_url + '/users', options);

    const res = await response.json();

    console.log(res)

    return res._id
  }

  const postMovieRequest = async (movie) => {
    let userID = getUserByID(props.list.user)

    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({"user": userID,
                            "list": props.list._id,
                            "rank": movie.rank,
                            "title": movie.title,
                            "year": movie.year,
                            "poster": movie.poster,
                            "review": movie.review
                          })
    }
    
    const response = await fetch(server_url + '/query/movies', options);

    const res = await response.json();

    console.log(res);
  }

  useEffect(() => {
    postMovieRequest(addMovie);
  }, [addMovie]);

    return (
      <div>
        <div className="Rankd-List">
          <h1 className="Large">{props.list.title}</h1>
          <h2 className="fix-h">{props.list.text}</h2>
          <h3>Created By: {props.list.username}</h3>

          <div className="display-movies">
            {props.list.movies !== null && props.list.movies.map((movie, index)=> (
              <DisplayMovie key={index} movie={movie} reset={props.reset} setReset={props.setReset}/>
            ))}
          </div>
        </div>

        <div className="add-movies">
          <div className="extra-margin">
            <h1>Add Movies</h1>
            <Search searchValue={props.searchValue} setSearchValue={props.setSearchValue}/>
            <List items={props.movies} addMovie={addMovie}  setAddMovie={setAddMovie} RnR={RankandReview} setRnR={setRnR}/>
          </div>
        </div>
      </div>
    )
}

export default Rank;
