import React, { useState, useEffect } from 'react';
import './App.css';
import PageH from "./Components/PageHeader";
import List from "./Components/List";
import Home from "./Components/Home";
import SignIn from "./Components/SignIn";
import SignUp from "./Components/SignUp";
import Lists from "./Components/Lists";

const server_url = 'http://localhost:11037';

const showHome = () => {
  if (window.location.pathname === '/') {
    return <Home />
  }
}

const showSignIn = () => {
  if (window.location.pathname === '/signin') {
    return <SignIn />
  }
}

const showSignUp = () => {
  if (window.location.pathname === '/signup') {
    return <SignUp />
  }
}

const showLists = () => {
  if (window.location.pathname === '/signup') {
    return <Lists movies={movies}/>
  }
}

function App() {
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  const getMovieRequest = async (searchValue) => {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'text/plain',
      },
      body: searchValue
    }

    const response = await fetch(server_url + '/query/movies', options);

    const res = await response.json();

    console.log(res);

    if (res.Search) {
      setMovies(res.Search);
    }
  }

  useEffect(() => {
    getMovieRequest(searchValue);
  }, [searchValue]);

  return (
    <div className="App">
      <PageH heading="Rankd" searchValue={searchValue} setSearchValue={setSearchValue}/>
      <div className="item-container">
        <List items={movies}/>
      </div>
    </div>
  );
}

export default App;
