import React, { useState, useEffect } from 'react';
import {BrowserRouter as Router, Switch, Route, Link} from 'react-router-dom';
import './App.css';
import PageH from "./Components/PageHeader";
import List from "./Components/List";

const server_url = 'http://localhost:11037';

function App() {
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  const getMovieRequest = async (searchValue) => {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'text/plain',
      },
      body: searchValue
    }

    const response = await fetch(server_url + '/query/movies', options);

    const res = await response.json();

    console.log(res);

    if (res.Search) {
      setMovies(res.Search);
    }
  }

  useEffect(() => {
    getMovieRequest(searchValue);
  }, [searchValue]);

  return (
    <Router>
      <div className="App">
        <PageH heading="Rankd" searchValue={searchValue} setSearchValue={setSearchValue}/>

        <Link to="/">Home</Link>
        <Link to="/signin">Sign In</Link>
        <List items={movies}/>
      </div>
    </Router>
  );
}

export default App;
