import React, { useState, useEffect } from 'react';
import './App.css';
import PageH from "./Components/PageHeader";
import List from "./Components/List";

const server_url = 'http://localhost:11037'

function App() {
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  const getMovieRequest = async (searchValue) => {
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: searchValue
    }

    const res = await fetch('http://localhost:11037/query/movies', options)
    .catch(err => console.log(err))

    console.log(res);

    if (res.Search) {
      setMovies(res.Search);
    }
  }

  useEffect(() => {
    getMovieRequest(searchValue);
  }, [searchValue]);

  return (
    <div className="App">
      <PageH heading="Rankd" searchValue={searchValue} setSearchValue={setSearchValue}/>
      <div className="item-container">
        <List items={movies}/>
      </div>
    </div>
  );
}

export default App;
