import React, { useState, useEffect } from 'react';
import MovieSetup from "./MovieSetup";

function CreateList(props) {
    const [newTitle, setTitle] = useState();
    const [newDesc, setDesc] = useState();
    const [submit, setSubmit] = useState(false);

    useEffect(() => {
        props.setRnR({"rank": newRank, "review": newReview});
        props.setTrigger(false);
        props.setAddMovie({"Title": props.movie.Title,
                           "Year": props.movie.Year,
                           "Poster": props.movie.Poster});
        setRank();
        setReview('');
    }, [submit]);

    return (
      <div className="CreateList">
        <h1 className="Large">Create List</h1>
        <h2>Title</h2>
        <input className="title" value={newTitle || ''} onChange={(event) => setTitle(event.target.value)}></input>
        <h2>Description</h2>
        <textarea className="desc" value={newDesc || ''} onChange={(event) => setDesc(event.target.value)}></textarea>
        <button onClick={setSubmit}>Add Movie</button>
      </div>
    )
}

export default CreateList;
