import React, { useState, useEffect } from 'react';

function MovieSetup(props) {
    const [newRank, setRank] = useState();
    const [newReview, setReview] = useState('');

    function handleRankChange(event) {
        setRank(event.target.value);
    }

    function handleReviewChange(event) {
        setReview(event.target.value);
    }

    const submitMovie = () => {
        if (newRank) {
            console.log("SUBMITTED");
            //props.setRnR({"rank": newRank, "review": newReview});
            props.setTrigger(false);
        } else {
            // Enter Valid Rank
        }
    }
    
    return (props.trigger) ? (
      <div className="pop">
        <div className="pop-in">
            <div className="setRank">
                <h1>Set Rank</h1>
                <input type="number" value={newRank || ''} onChange={(rankevent) => setRank(rankevent.target.value)}></input>
            </div>

            <div className="setReview">
                <h1>Optional: Create Review</h1>
                <input value={newReview} onChange={(reviewevent) => setReview(reviewevent.target.value)}></input>
            </div>

            <button onClick={submitMovie()}>Add Movie</button>
        </div>
      </div>
    ) : ''
}

export default MovieSetup;
